getContact(): Observable<any> {
 return this.fetchJSON<any>('contact').pipe(
 map((data) => ({
 title: data.TITLE || '',
 description: {
 line1: data.DESCRIPTION?.LINE_1 || '',
 line2: data.DESCRIPTION?.LINE_2 || '',
 },
 form: {
 name: {
 label: data.FORM?.NAME?.LABEL || '',
 placeholder: data.FORM?.NAME?.PLACEHOLDER || '',
 requiredError: data.FORM?.NAME?.REQUIRED_ERROR || '',
 },
 email: {
 label: data.FORM?.EMAIL?.LABEL || '',
 placeholder: data.FORM?.EMAIL?.PLACEHOLDER || '',
 requiredError: data.FORM?.EMAIL?.REQUIRED_ERROR || '',
 },
 message: {
 label: data.FORM?.MESSAGE?.LABEL || '',
 placeholder: data.FORM?.MESSAGE?.PLACEHOLDER || '',
 requiredError: data.FORM?.MESSAGE?.REQUIRED_ERROR || '',
 },
 submitButton: data.FORM?.SUBMIT_BUTTON || '',
 },
 contactInfo: {
 emailLabel: data.CONTACT_INFO?.EMAIL_LABEL || '',
 email: data.CONTACT_INFO?.EMAIL || '',
 telLabel: data.CONTACT_INFO?.TEL_LABEL || '',
 tel: data.CONTACT_INFO?.TEL || '',
 },
 }))
 );
 }

